      [7] = { str = "06) Go in Anvilmar at 28,69 and turn in your classes skill npc quest which was Encrypted Rune for a rogue", x = 28, y = 69, zone = "Dun Morogh" },
      [8] = { str = "07) Accept A Refugee's Quandary inside " },
